function MyPanel($scope) {
    $scope.mypanel = {msg: "Hola!"};

    $scope.googleChart = {};

    $scope.drawChart = function() {
        $scope.googleChart.data = google.visualization.arrayToDataTable([
          ['Label', 'Value'],
          ['Memory', 80],
          ['CPU', 55],
          ['Network', 68]
        ]);
        
        $scope.googleChart.options = {
          width: 400, height: 120,
          redFrom: 90, redTo: 100,
          yellowFrom:75, yellowTo: 90,
          minorTicks: 5
        };

        $scope.googleChart.chart = new google.visualization.Gauge(document.getElementById('chart_div'));
        $scope.googleChart.chart.draw($scope.googleChart.data, $scope.googleChart.options);
    };
}

google.load('visualization', '1', {packages:['gauge']});

google.setOnLoadCallback(function() {
    angular.bootstrap(document.body, ['PyArduino']);
});
